    {scriptId: '12', functions: []},
    {scriptId: '13', functions: []},
    {scriptId: '16', functions: []},
    {scriptId: '17', functions: []},
  ],
  'https://example.com/2.js': [
    {scriptId: '22', functions: []},
    {scriptId: '23', functions: []},
